INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (7, 'Верховный Смотритель Кэмпбелл', 'Смотритель', 'Вечнопритоки', 'Dishonored', '/assets/img/characters/campbell.png', 'Глава Ордена Аббатства Вечного Хора. Религиозный фанатик и заговорщик. Участвовал в убийстве императрицы. Использовал Орден для своих политических целей.', '{"Во имя Аббатства!","Еретики должны быть наказаны","Аутсайдер - источник всего зла"}', 'Убит или заклеймён как еретик', '2026-01-31 03:04:15.555579', 'Дэниел Хейген', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (9, 'Эмили Калдвин', 'Дочь императрицы', 'Империя', 'Dishonored', '/assets/img/characters/emily_child.png', 'Дочь императрицы Джесамин Калдвин и Корво Аттано. После смерти матери находилась под опекой Корво. Наследница трона Островной Империи. Стала свидетелем убийства матери.', '{"Корво! Я знала, что ты придёшь!","Расскажи мне про маму","Когда я стану императрицей, всё будет по-другому"}', 'Жива', '2026-01-31 03:04:15.555579', 'Хлоя Грэйс Морец', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (11, 'Делайла Копперспун', 'Самозванка-императрица', 'Ведьмы Бригмора', 'Dishonored 2', '/assets/img/characters/delilah.png', 'Самозванка-императрица, обладающая магическими способностями. Сводная сестра Джесамин Калдвин. Захватила трон с помощью колдовства и превратила Дунвольд в кошмар.', '{"Я создам мир по своему образу","Трон принадлежит мне по праву рождения","Они забыли обо мне, но я не забыла"}', 'Побеждена Эмили/Корво', '2026-01-31 03:04:15.561037', 'Пола Маркс', 'Dishonored: DLC (упоминание)', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (12, 'Киррин Джиндош', 'Изобретатель', 'Независимый', 'Dishonored 2', '/assets/img/characters/jindosh.png', 'Гениальный изобретатель и учёный Карнаки. Создатель Клокворк-солдат. Служит Делайле. Его особняк полон механических ловушек и изобретений.', '{"Совершенство механики!","Мои творения непобедимы","Наука превыше всего"}', 'Убит или лишён разума', '2026-01-31 03:04:15.561037', 'Педро Паскаль', 'Dishonored 2', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (13, 'Брендан Байрн', 'Глава Великой стражи', 'Вечнопритоки', 'Dishonored 2', '/assets/img/characters/byrne.png', 'Герцог Серконоса, правитель южных земель. Союзник Делайлы. Организовал переворот против Эмили. Устраивает жестокие кровавые бои.', '{"Добро пожаловать в Серконос","Здесь правлю я","Кровь и власть - вот что важно"}', 'Убит или свергнут', '2026-01-31 03:04:15.561037', 'Сэм Рокуэлл', 'Dishonored 2', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (15, 'Миган Фостер', 'Капитан', 'Вечнопритоки', 'Dishonored 2', '/assets/img/characters/meagan.png', 'Капитан корабля "Ужасный Герцог", на самом деле Билли Лёрк под псевдонимом. Помогает Эмили и Корво сбежать из Дунвольда.', '{"На корабль! Быстрее!","Море - мой дом","Доверьтесь мне"}', 'Жива (на самом деле Билли Лёрк)', '2026-01-31 03:04:15.561037', 'Розарио Доусон', 'Dishonored 2', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (16, 'Аутсайдер', 'Божество пустоты', 'Независимая', 'Dishonored 2', '/assets/img/characters/outsider2.png', 'Загадочное божество Бездны. Продолжает наблюдать за судьбами избранных. Дарует Эмили её уникальные способности, если она принимает его дар.', '{"Интересно, что ты выберешь на этот раз","Империя в огне. Что ты будешь делать?","Твоя судьба - в твоих руках"}', 'Бессмертен', '2026-01-31 03:04:15.561037', 'Робин Лорд Тейлор', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (2, 'Аутсайдер', 'Божество пустоты', 'Независимая', 'Dishonored', '/assets/img/characters/outsider.png', 'Загадочное божество Бездны, существующее вне времени и пространства. Дарует избранным смертным сверхъестественные способности, наблюдая за их судьбами. Проявляет интерес к Корво Аттано и его выборам.', '{"Твоё сердце бьётся чаще, когда я рядом","Я дам тебе силу. Что ты с ней сделаешь - твой выбор","Наблюдать за тобой... интересно"}', 'Бессмертен', '2026-01-31 03:04:15.555579', 'Билли Люш', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (4, 'Пьеро Джоплин', 'Королевский врач', 'Верные', 'Dishonored', '/assets/img/characters/piero.png', 'Королевский врач и изобретатель. Гений механики и алхимии. Создатель многих устройств для Корво, включая складную маску и улучшения оружия. Член группы Лоялистов.', '{"Наука и магия - две стороны одной медали","Я создал для вас кое-что интересное","Эти изобретения помогут вам в миссии"}', 'Жив', '2026-01-31 03:04:15.555579', 'Брэд Дуриф', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (5, 'Антон Соколов', 'Художник и учёный', 'Китобои', 'Dishonored', '/assets/img/characters/sokolov.png', 'Королевский врач, художник, философ и учёный. Гений своего времени. Работал на лорда-регента, изучая чуму. Позже помогал Корво в его миссии возмездия. Создатель многих изобретений.', '{"Соколов создал это!","Я посвятил жизнь изучению природы вещей","Искусство и наука неразделимы"}', 'Жив', '2026-01-31 03:04:15.555579', 'Роджер Л. Джексон', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (19, 'Эмили Калдвин', 'Императрица', 'Империя', 'Dishonored 2', '/assets/img/characters/emily.png', 'Дочь Корво Аттано и Джесамин Калдвин. Императрица Островной Империи. После государственного переворота была вынуждена бежать из Дунволла. Обладает уникальными способностями, дарованными Аутсайдером.', '{"Мне не нужна корона, чтобы быть сильной","Я не просто императрица. Я воин","Они забыли, что я дочь Корво Аттано"}', 'Жива', '2026-01-31 03:04:15.561037', 'Эрика Латтрелл', 'Dishonored (ребенок)', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (17, 'Корво Аттано', 'Королевский защитник', 'Империя', 'Dishonored 2', '/assets/img/characters/corvo2.png', 'Королевский Защитник, отец Эмили Калдвин. Легендарный убийца, который остановил заговор лорда-регента. Теперь защищает свою дочь и помогает вернуть ей трон.', '{"Я защищал императрицу. Теперь защищаю свою дочь","Месть - это то, чем я занимаюсь лучше всего","За Эмили"}', 'Жив', '2026-01-31 03:04:15.561037', 'Стивен Рассел', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (10, 'Билли Лёрк', 'Капитан корабля', 'Независимая', 'Dishonored 2', '/assets/img/characters/billie.png', 'Бывшая правая рука Дауда, капитан корабля "Трепанация". Помогает Эмили и Корво в их миссии вернуть трон. Потеряла руку и глаз в прошлом.', '{"Готовы отплывать?","Я многое повидала, но это...","Дауд многому меня научил"}', 'Жива', '2026-01-31 03:04:15.561037', 'Розарио Доусон', 'Dishonored 2', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (14, 'Александрия Хайпейша', 'Доктор', 'Культ Аутсайдера', 'Dishonored 2', '/assets/img/characters/hypatia.png', 'Доктор и алхимик Аддермайрского института. Создала раствор Аддермайра. Проводит жестокие эксперименты над пациентами по приказу короны.', '{"Во имя науки","Прогресс требует жертв","Мои исследования важнее всего"}', 'Убита или арестована', '2026-01-31 03:04:15.561037', 'Андреа Райзборо', 'Dishonored 2', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (18, 'Антон Соколов', 'Художник и учёный', 'Независимая', 'Dishonored 2', '/assets/img/characters/sokolov2.png', 'Легендарный учёный, художник и изобретатель. Теперь пожилой, но всё ещё гениальный. Помогает Эмили и Корво советами и изобретениями.', '{"Годы не остановили мой разум","Я всё ещё могу удивить мир","Эмили, ты выросла сильной"}', 'Жив', '2026-01-31 03:04:15.561037', 'Роджер Л. Джексон', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (3, 'Дауд', 'Наёмный убийца', 'Китобои', 'Dishonored', '/assets/img/characters/daud.png', 'Легендарный убийца, лидер китобоев. Убил императрицу Джесамин Калдвин по заказу. Позже раскаялся в содеянном и пытался искупить свою вину.', '{"Моё имя - Дауд. И я убил императрицу","Я много убивал. Но это убийство изменило всё","Искупление... возможно ли оно для таких, как я?"}', 'Жив (на момент Dishonored 2)', '2026-01-31 03:04:15.555579', 'Майкл Мадсен', 'Dishonored', NULL);
INSERT INTO public.characters (id, name, role, faction, game, image, description, quotes, status, created_at, voice_actor, first_appearance, faction_id) VALUES (1, 'Корво Аттано', 'Королевский защитник', 'Лоялисты', 'Dishonored', '/assets/img/characters/corvo.png', 'Королевский Защитник императрицы Джесамин Калдвин. Ложно обвинён в её убийстве и заключён в тюрьму Колдридж. Сбегает с помощью Лоялистов и получает силы от Аутсайдера, чтобы отомстить заговорщикам.', '{"За императрицу","Месть решает всё","Я был обманут. Преда"}', 'Жив', '2026-01-31 03:04:15.555579', 'Не озвучен (Dishonored 1)', 'Dishonored', NULL);
