INSERT INTO public.game_mechanics (id, title, category, game, description, effects, tips, created_at, image) VALUES (2, 'Стелс-геймплей', 'stealth', 'Dishonored', 'Скрытность - ключ к успеху. Избегайте обнаружения, используя тени, способности и окружение.', '{"Обнаружение повышает сложность","Тени скрывают от врагов","Звуки привлекают внимание","Разные пути прохождения уровней"}', '{"Используйте Тёмное зрение для планирования","Ждите патрули врагов","Перемещайтесь по крышам","Отключайте источники света"}', '2026-01-31 00:34:17.811333', NULL);
INSERT INTO public.game_mechanics (id, title, category, game, description, effects, tips, created_at, image) VALUES (3, 'Достижения Ghost и Clean Hands', 'achievements', 'Dishonored', 'Особые достижения за прохождение без убийств и обнаружений.', '{"Ghost: не быть замеченным","Clean Hands: никого не убивать","Требует идеального планирования","Награждается уникальными концовками"}', '{"Сохраняйтесь часто","Изучайте карты перед действием","Используйте способности для стелса","Нокаутированные враги не считаются убийством"}', '2026-01-31 00:34:17.811333', NULL);
INSERT INTO public.game_mechanics (id, title, category, game, description, effects, tips, created_at, image) VALUES (5, 'Руны и улучшения', 'progression', 'Dishonored', 'Руны используются для получения и улучшения сверхъестественных способностей.', '{"Находятся с помощью Сердца","Каждая способность имеет уровни","Ограниченное количество рун в игре","Влияют на стиль прохождения"}', '{"Планируйте развитие заранее","Исследуйте уровни полностью","Разные билды для разных стилей"}', '2026-01-31 00:34:17.811333', NULL);
INSERT INTO public.game_mechanics (id, title, category, game, description, effects, tips, created_at, image) VALUES (1, 'Система Хаоса', 'chaos', 'Dishonored', 'Уровень хаоса определяет мир вокруг вас и влияет на концовку игры. Чем больше людей вы убиваете, тем выше хаос.', '{"Низкий хаос: меньше крыс и жуков, светлая концовка","Высокий хаос: больше врагов, тёмная концовка","Влияет на поведение союзников","Изменяет диалоги и реакции NPC"}', '{"Используйте нелетальные методы для низкого хаоса","Прячьте тела убитых врагов","Используйте сон-дротики вместо смертельных"}', '2026-01-31 00:34:17.811333', '/assets/img/mechanics/chaos.png');
INSERT INTO public.game_mechanics (id, title, category, game, description, effects, tips, created_at, image) VALUES (4, 'Костяные амулеты', 'customization', 'Dishonored', 'Магические амулеты, дающие пассивные бонусы и улучшения.', '{"Находятся по всему миру","Дают различные пассивные эффекты","Можно носить до 6 одновременно","В D2 можно создавать собственные"}', '{"Используйте Сердце для поиска амулетов","Выбирайте амулеты под свой стиль игры","Комбинируйте эффекты для синергии"}', '2026-01-31 00:34:17.811333', '/assets/img/mechanics/bone_charms.png');
INSERT INTO public.game_mechanics (id, title, category, game, description, effects, tips, created_at, image) VALUES (6, 'Система Хаоса', 'chaos', 'Dishonored 2', 'Улучшенная система хаоса с более детализированным влиянием на мир. Каждое убийство и обнаружение влияет на концовку игры.', '{"Низкий хаос: меньше врагов, светлая концовка","Высокий хаос: больше кровомух и крыс, тёмная концовка","Влияет на диалоги и реакции персонажей","Более детальная система чем в первой игре"}', '{"Используйте нелетальные методы для низкого хаоса","Прячьте тела убитых врагов","Избегайте случайных убийств","Используйте усыпляющие дротики"}', '2026-01-31 06:03:13.892902', NULL);
